+
+func TestMigrateToNetbird(t *testing.T) {
+	// Create temporary directory for test files
+	tempDir, err := os.MkdirTemp("", "netbird-test")
+	if err != nil {
+		t.Fatalf("Failed to create temp dir: %v", err)
+	}
+	defer os.RemoveAll(tempDir)
+
+	oldPath := filepath.Join(tempDir, "old-config.json")
+	newPath := filepath.Join(tempDir, "new-config.json")
+
+	tests := []struct {
+		name     string
+		oldExist bool
+		newExist bool
+		want     bool
+	}{
+		{
+			name:     "Both paths exist",
+			oldExist: true,
+			newExist: true,
+			want:     false, // No migration needed
+		},
+		{
+			name:     "Only old path exists",
+			oldExist: true,
+			newExist: false,
+			want:     true, // Migration needed
+		},
+		{
+			name:     "Only new path exists",
+			oldExist: false,
+			newExist: true,
+			want:     false, // No migration needed
+		},
+		{
+			name:     "Neither path exists",
+			oldExist: false,
+			newExist: false,
+			want:     false, // No migration needed
+		},
+	}
+
+	for _, tt := range tests {
+		t.Run(tt.name, func(t *testing.T) {
+			// Clean up from previous test
+			os.Remove(oldPath)
+			os.Remove(newPath)
+
+			// Create test files as needed
+			if tt.oldExist {
+				if err := os.WriteFile(oldPath, []byte("test"), 0644); err != nil {
+					t.Fatalf("Failed to create old file: %v", err)
+				}
+			}
+			if tt.newExist {
+				if err := os.WriteFile(newPath, []byte("test"), 0644); err != nil {
+					t.Fatalf("Failed to create new file: %v", err)
+				}
+			}
+
+			// Test the function
+			if got := migrateToNetbird(oldPath, newPath); got != tt.want {
+				t.Errorf("migrateToNetbird() = %v, want %v", got, tt.want)
+			}
+		})
+	}
+}

