+package cmd
+
+import (
+	"testing"
+)
+
+func TestValidateElement(t *testing.T) {
+	tests := []struct {
+		name           string
+		element        string
+		expectedType   int
+		expectedErrMsg string
+	}{
+		{
+			name:         "Valid IP",
+			element:      "192.168.1.1",
+			expectedType: ipInputType,
+		},
+		{
+			name:           "Invalid IP and non-existent interface",
+			element:        "not-an-ip-or-interface",
+			expectedType:   invalidInputType,
+			expectedErrMsg: "invalid IP or network interface name not found",
+		},
+	}
+
+	for _, tt := range tests {
+		t.Run(tt.name, func(t *testing.T) {
+			gotType, gotErr := validateElement(tt.element)
+			
+			if gotType != tt.expectedType {
+				t.Errorf("validateElement() type = %v, want %v", gotType, tt.expectedType)
+			}
+			
+			if tt.expectedErrMsg != "" {
+				if gotErr == nil {
+					t.Errorf("validateElement() expected error with message %q, got nil", tt.expectedErrMsg)
+				} else if gotErr.Error() != tt.expectedErrMsg {
+					t.Errorf("validateElement() error message = %q, want %q", gotErr.Error(), tt.expectedErrMsg)
+				}
+			} else if gotErr != nil {
+				t.Errorf("validateElement() unexpected error = %v", gotErr)
+			}
+		})
+	}
+}
