+package internal
+
+import (
+	"context"
+	"testing"
+)
+
+func TestCtxGetState(t *testing.T) {
+	t.Run("Should return state when context has state", func(t *testing.T) {
+		// Initialize context with state
+		ctx := CtxInitState(context.Background())
+		
+		// Get state from context
+		state := CtxGetState(ctx)
+		
+		// Verify state is not nil
+		if state == nil {
+			t.Error("Expected state to be not nil")
+		}
+		
+		// Verify initial status
+		status, err := state.Status()
+		if err != nil {
+			t.Errorf("Expected no error, got %v", err)
+		}
+		if status != StatusIdle {
+			t.Errorf("Expected status to be %s, got %s", StatusIdle, status)
+		}
+	})
+	
+	t.Run("Should return nil when context has no state", func(t *testing.T) {
+		// Create context without state
+		ctx := context.Background()
+		
+		// Get state from context
+		state := CtxGetState(ctx)
+		
+		// Verify state is nil
+		if state != nil {
+			t.Error("Expected state to be nil")
+		}
+	})
+	
+	t.Run("Should return nil when context has wrong type", func(t *testing.T) {
+		// Create context with wrong type
+		ctx := context.WithValue(context.Background(), stateCtx, "not a state")
+		
+		// Get state from context
+		state := CtxGetState(ctx)
+		
+		// Verify state is nil
+		if state != nil {
+			t.Error("Expected state to be nil")
+		}
+	})
+}
+
+func TestContextState(t *testing.T) {
+	t.Run("Should set and get status", func(t *testing.T) {
+		// Initialize context with state
+		ctx := CtxInitState(context.Background())
+		state := CtxGetState(ctx)
+		
+		// Set status
+		state.Set(StatusConnecting)
+		
+		// Get status
+		status, err := state.Status()
+		if err != nil {
+			t.Errorf("Expected no error, got %v", err)
+		}
+		if status != StatusConnecting {
+			t.Errorf("Expected status to be %s, got %s", StatusConnecting, status)
+		}
+	})
+	
+	t.Run("Should wrap error", func(t *testing.T) {
+		// Initialize context with state
+		ctx := CtxInitState(context.Background())
+		state := CtxGetState(ctx)
+		
+		// Set error
+		testErr := state.Wrap(context.DeadlineExceeded)
+		
+		// Verify returned error
+		if testErr != context.DeadlineExceeded {
+			t.Errorf("Expected error to be %v, got %v", context.DeadlineExceeded, testErr)
+		}
+		
+		// Get status with error
+		status, err := state.Status()
+		if err != context.DeadlineExceeded {
+			t.Errorf("Expected error to be %v, got %v", context.DeadlineExceeded, err)
+		}
+		if status != "" {
+			t.Errorf("Expected status to be empty, got %s", status)
+		}
+	})
+}
