 };

 export function absolutifyLink(rel: string): string {
+  // Validate URL to prevent javascript: URLs
+  if (rel.toLowerCase().trim().startsWith('javascript:')) {
+    return '#';
+  }
+  
   const anchor = document.createElement("a");
   anchor.setAttribute("href", rel);
+  
+  // Double-check the resulting URL to ensure it's not a javascript: URL
+  // This catches cases where the URL might be encoded or otherwise obfuscated
+  if (anchor.href.toLowerCase().trim().startsWith('javascript:')) {
+    return '#';
+  }
+  
   return anchor.href;
 }

